// components/header.js
import { Link } from 'react-router-dom';
import React, { useContext } from 'react';
import { UserContext } from '../App';

const Header = () => {
    const { user, logout } = useContext(UserContext);

    return (
        <header style={styles.header}>
            <nav>
                <ul style={styles.navList}>
                    <li style={styles.navItem}><Link to="/">Home</Link></li>
                    <li style={styles.navItem}><Link to="/blog">Blog</Link></li>
                    <li style={styles.navItem}><Link to="/about">About</Link></li>
                    <li style={styles.navItem}><Link to="/contact">Contact</Link></li>
                    {user ? (
                        <>
                            <li style={styles.navItem}><Link to="/profile">Profile</Link></li>
                            <li style={styles.navItem}><button onClick={logout}>Logout</button></li>
                        </>
                    ) : (
                        <li style={styles.navItem}><Link to="/login">Login</Link></li>
                    )}
                </ul>
            </nav>
        </header>
    );
};
const styles = {
    header: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '20px',
        background: '#f2f2f2', // Пастельный тон для фона хедера
    },
    logo: {
        fontSize: '24px',
        fontWeight: 'bold',
        textDecoration: 'none',
        color: '#333',
    },
    navContainer: {
        display: 'flex',
    },
    navButton: {
        padding: '10px 20px',
        margin: '0 10px',
        borderRadius: '5px',
        backgroundColor: '#5eaaa8', // Пастельный тон для кнопок
        color: '#fff',
        cursor: 'pointer',
        textDecoration: 'none',
        textAlign: 'center',
    },
};

export default Header;
