// Header.js
import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { UserContext } from '../App';

const Header = () => {
    const { user, logout } = useContext(UserContext);
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate('/login'); // Перенаправляем пользователя на страницу входа после выхода
    };

    return (
        <header style={styles.header}>
            <div style={styles.buttonContainer}>
                <div style={styles.navItem}>
                    {user ? (
                        <>
                            <button onClick={() => navigate('/profile')} style={styles.button}>Profile</button>
                            <button onClick={handleLogout} style={styles.logoutButton}>Logout</button>
                        </>
                    ) : (
                        <>
                            <button onClick={() => navigate('/login')} style={styles.button}>Login</button>
                            <button onClick={() => navigate('/register')} style={styles.button}>Register</button>
                        </>
                    )}
                </div>
            </div>
            <nav>
                <ul style={styles.navList}>
                    <li style={styles.navItem}><Link to="/">Home</Link></li>
                    <li style={styles.navItem}><Link to="/blog">Blog</Link></li>
                    <li style={styles.navItem}><Link to="/about">About</Link></li>
                    <li style={styles.navItem}><Link to="/contact">Contact</Link></li>
                </ul>
            </nav>
        </header>
    );
};

const styles = {
    header: {
        backgroundColor: '#5eaaa8',
        padding: '10px',
        marginBottom: '20px',
    },
    navList: {
        listStyle: 'none',
        display: 'flex',
        justifyContent: 'space-around',
        alignItems: 'center',
    },
    navItem: {
        margin: '0 10px',
    },
    buttonContainer: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
    },
    button: {
        padding: '8px 16px',
        borderRadius: '5px',
        backgroundColor: '#5eaaa8',
        color: '#fff',
        textDecoration: 'none',
        cursor: 'pointer',
        border: 'none',
        marginRight: '10px',
    },
    logoutButton: {
        padding: '8px 16px',
        borderRadius: '5px',
        backgroundColor: '#e63946',
        color: '#fff',
        cursor: 'pointer',
        border: 'none',
    },
};

export default Header;
