// App.js

import React, { createContext, useContext, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, Outlet } from 'react-router-dom';
import Home from './pages/Home';
import Blog from './pages/Blog';
import About from './pages/About';
import Contact from './pages/Contact';
import RegistrationForm from './pages/registrationform';
import Profile from './pages/Profile';
import Header from './components/header';
import Footer from './components/footer';

const UserContext = createContext();

const App = () => {
    const [user, setUser] = useState(null);

    const setAuthenticatedUser = (userData) => {
        setUser(userData);
    };

    const contextValue = {
        user,
        setAuthenticatedUser,
    };

    const UserProvider = ({ children }) => (
        <UserContext.Provider value={contextValue}>{children}</UserContext.Provider>
    );

    const ProtectedRoute = ({ element }) => {
        const { user } = useContext(UserContext);
        return user ? element : <Navigate to="/login" />;
    };

    return (
        <Router>
            <UserProvider>
                <div style={styles.container}>
                    <Header/>
                    <div id="content" style={styles.content}>
                        <Routes>
                            <Route path="/" element={<Home style={styles.route}/>}/>
                            <Route path="/blog" element={<Blog style={styles.route}/>}/>
                            <Route path="/about" element={<About style={styles.route}/>}/>
                            <Route path="/contact" element={<Contact style={styles.route}/>}/>
                            <Route path="/register" element={<RegistrationForm style={styles.route}/>}/>
                            <Route path="/profile" element={<ProtectedRoute element={<Profile/>} style={styles.route}/>}/>
                            <Route path="/login" element={<Login style={styles.route}/>}/>
                        </Routes>
                    </div>
                    <Footer/>
            </div>
        </UserProvider>
</Router>
)
    ;
};

export default App;

const Login = () => {
    const {setAuthenticatedUser} = useContext(UserContext);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async () => {
        try {
            const response = await fetch('http://localhost:3001/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({username, password}),
            });

            if (response.ok) {
                const userData = await response.json();
                setAuthenticatedUser(userData);
            } else {
                console.error('Login failed:', response.statusText);
            }
        } catch (error) {
            console.error('Login error:', error);
        }
    };

    return (
        <div>
            <h2>Login Page</h2>
            <label>
                Username:
                <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
            </label>
            <br />
            <label>
                Password:
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
            </label>
            <br />
            <button onClick={handleLogin}>Login</button>
        </div>
    );
};

const styles = {
    container: {
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        backgroundImage: 'url("путь_к_вашему_изображению.jpg")',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
    },
    content: {
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'space-around', // Изменили на space-around для равномерного распределения
        width: '80%', // Вы можете настроить ширину под свои требования
        margin: '0 auto', // Чтобы выровнять по центру
    },
    route: {
        padding: '10px', // Добавлены отступы для красивого отображения
    },
};

export { UserContext };
