// Header.js
import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { UserContext } from '../App';

const Header = () => {
    const { user, logout } = useContext(UserContext);

    return (
        <header style={styles.header}>
            <nav>
                <ul style={styles.navList}>
                    <li style={styles.navItem}><Link to="/">Home</Link></li>
                    <li style={styles.navItem}><Link to="/blog">Blog</Link></li>
                    <li style={styles.navItem}><Link to="/about">About</Link></li>
                    <li style={styles.navItem}><Link to="/contact">Contact</Link></li>
                    {user ? (
                        <>
                            <li style={styles.navItem}><Link to="/profile">Profile</Link></li>
                            <li style={styles.navItem}><button onClick={logout} style={styles.logoutButton}>Logout</button></li>
                        </>
                    ) : (
                        <>
                            <li style={styles.navItem}><Link to="/login">Login</Link></li>
                            <li style={styles.navItem}><Link to="/register">Register</Link></li>
                        </>
                    )}
                </ul>
            </nav>
        </header>
    );
};

const styles = {
    header: {
        backgroundColor: '#5eaaa8', // Цвет хедера
        padding: '10px',
        marginBottom: '20px',
    },
    navList: {
        listStyle: 'none',
        display: 'flex',
        justifyContent: 'space-around',
        alignItems: 'center',
    },
    navItem: {
        margin: '0 10px',
    },
    logoutButton: {
        padding: '8px 16px',
        borderRadius: '5px',
        backgroundColor: '#e63946', // Пастельный тон для кнопок
        color: '#fff',
        cursor: 'pointer',
        border: 'none',
    },
};

export default Header;
