// RegistrationForm.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const RegistrationForm = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    // Получаем функцию navigate из react-router-dom
    const navigate = useNavigate();

    const handleRegistration = () => {
        // Здесь вы можете добавить логику для отправки данных на сервер
        // и выполнения процесса регистрации
        console.log('Регистрация:', { username, password });

        // После успешной регистрации переходим на главную страницу
        navigate('/');
    };

    return (
        <div>
            <h2>Регистрация</h2>
            <form>
                <label>
                    Имя пользователя:
                    <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
                </label>
                <br />
                <label>
                    Пароль:
                    <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                </label>
                <br />
                <button type="button" onClick={handleRegistration}>
                    Зарегистрироваться
                </button>
            </form>
        </div>
    );
};

export default RegistrationForm;
