// Header.js
import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { UserContext } from '../App';

const Header = () => {
    const { user, logout } = useContext(UserContext);

    return (
        <header style={styles.header}>
            <nav>
                <div style={styles.buttonContainer}>
                    <div style={styles.navItem}>
                        {user ? (
                            <>
                                <Link to="/profile" style={styles.button}>Profile</Link>
                                <button onClick={logout} style={styles.logoutButton}>Logout</button>
                            </>
                        ) : (
                            <>
                                <Link to="/login" style={styles.button}>Login</Link>
                                <Link to="/register" style={styles.button}>Register</Link>
                            </>
                        )}
                    </div>
                </div>
                <ul style={styles.navList}>
                    <li style={styles.navItem}><Link to="/">Home</Link></li>
                    <li style={styles.navItem}><Link to="/blog">Blog</Link></li>
                    <li style={styles.navItem}><Link to="/about">About</Link></li>
                    <li style={styles.navItem}><Link to="/contact">Contact</Link></li>
                </ul>
            </nav>
        </header>
    );
};

const styles = {
    header: {
        backgroundColor: '#5eaaa8', // Цвет хедера
        padding: '10px',
        marginBottom: '20px',
    },
    navList: {
        listStyle: 'none',
        display: 'flex',
        justifyContent: 'space-around',
        alignItems: 'center',
    },
    navItem: {
        margin: '0 10px',
    },
    buttonContainer: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
    },
    button: {
        padding: '8px 16px',
        borderRadius: '5px',
        backgroundColor: '#5eaaa8', // Пастельный тон для кнопок
        color: '#fff',
        textDecoration: 'none',
        cursor: 'pointer',
        border: 'none',
    },
    logoutButton: {
        padding: '8px 16px',
        borderRadius: '5px',
        backgroundColor: '#e63946', // Пастельный тон для кнопок
        color: '#fff',
        cursor: 'pointer',
        border: 'none',
    },
};

export default Header;
