// components/header.js
import React from 'react';
import { Link } from 'react-router-dom';

const Header = () => {
    const styles = {
        header: {
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            padding: '20px',
            background: '#f2f2f2', // Пастельный тон для фона хедера
        },
        logo: {
            fontSize: '24px',
            fontWeight: 'bold',
            textDecoration: 'none',
            color: '#333',
        },
        navContainer: {
            display: 'flex',
        },
        navButton: {
            padding: '10px 20px',
            margin: '0 10px',
            borderRadius: '5px',
            backgroundColor: '#5eaaa8', // Пастельный тон для кнопок
            color: '#fff',
            cursor: 'pointer',
            textDecoration: 'none',
            textAlign: 'center',
        },
    };

    return (
        <header style={styles.header}>
            <Link to="/" style={styles.logo}>
                Your Logo
            </Link>
            <nav style={styles.navContainer}>
                <Link to="/" style={styles.navButton}>Home</Link>
                <Link to="/blog" style={styles.navButton}>Blog</Link>
                <Link to="/about" style={styles.navButton}>About</Link>
                <Link to="/contact" style={styles.navButton}>Contact</Link>
                <Link to="/register" style={styles.navButton}>Register</Link>
                <Link to="/profile" style={styles.navButton}>Profile</Link>
                <Link to="/login" style={styles.navButton}>Login</Link>
            </nav>
        </header>
    );
};

export default Header;
