import React from 'react';

function About() {
    return (
        <div>
            <h1>About Us</h1>
            <p>
                My Blog Platform is a community-driven platform where passionate individuals share their knowledge
                and experience in various fields such as programming, design, and technology.
            </p>
            <p>
                Our mission is to create a space for learning, collaboration, and inspiration.
            </p>
        </div>
    );
}

export default About;
